import React, { Component } from "react";
import { Link } from "react-router-dom";
import uuid from "uuid";
import axios from "axios";
import "./Navbar.css";

class AllContacts extends Component {
  state = {
    contacts: [
      // {
      //   id: uuid(),
      //   fullname: "Imonjirie Newton",
      //   email: "newtonimonjirie@gmail.com",
      //   phone_number: "09057387046",
      //   address: "Mayfair Gardens, Lekki"
      // },
      // {
      //   id: uuid(),
      //   fullname: "Osinachi Jane",
      //   email: "nsiegbunam.osinachijane@gmail.com",
      //   phone_number: "07065602386",
      //   address: "Starways Estate, Ajah"
      // },
      // {
      //   id: uuid(),
      //   fullname: "Uche Philips",
      //   email: "uchePhilz@gmail.com",
      //   phone_number: "07045342311",
      //   address: "Adejose Close, Vi"
      // }
    ]
  };
  componentDidMount() {
    axios
      .get("/api/contacts")
      .then(res => this.setState({ contacts: res.data }));
  }
  render() {
    var contact = (
      <div className="main">
        <div className="container">
          <table
            style={{ alignItems: "left" }}
            width="50%"
            className="table table-bordered table-hover"
          >
            <thead>
              <tr align="left">
                <th>Full Name</th>
                <th>Email Address</th>
                <th>Phone Number</th>
                <th>Residential Address</th>
              </tr>
            </thead>
            <tbody>
              {this.state.contacts.map(contact => (
                <tr align="left" key={contact.id}>
                  <td>{contact.full_name}</td>
                  <td>{contact.email}</td>
                  <td>{contact.phone_number}</td>
                  <td>{contact.address}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
    var no_contact = (
      <div className="main">
        <h1 style={{ color: "##d3d3d3" }}>No Contacts Available.....</h1>
      </div>
    );
    return this.state.contacts.length > 0 ? contact : no_contact;
  }
}

export default AllContacts;
