import React, { Component } from "react";
import { Link } from "react-router-dom";
import ReactPaginate from "react-paginate";
import axios from "axios";
import "./Navbar.css";

class AllContacts extends Component {
  state = {
    contacts: []
  };
  componentDidMount() {
    this.getContacts();
  }

  getContacts = () => {
    axios.get("/api/contacts").then(res =>
      this.setState({
        contacts: res.data,
        pageCount: Math.ceil(contacts.meta.total_count / contacts.meta.limit)
      })
    );
  };

  onDelete = id => {
    axios.delete(`/api/contacts/delete/${id}`);
    this.setState({
      contacts: this.state.contacts.filter(contact => contact._id !== id)
    });
  };

  render() {
    var contact = (
      <div>
        <table
          style={{ alignItems: "left" }}
          width="50%"
          className="table table-bordered table-hover"
        >
          <thead>
            <tr align="left">
              <th>Full Name</th>
              <th>Email Address</th>
              <th>Phone Number</th>
              <th>Residential Address</th>
            </tr>
          </thead>
          <tbody>
            {this.state.contacts.map(contact => (
              <tr align="left" key={contact._id}>
                <td>
                  <Link key={contact._id} to={`/view/${contact._id}`}>
                    {contact.full_name}
                  </Link>
                </td>
                <td width="150">{contact.email}</td>
                <td>{contact.phone_number}</td>
                <td>{contact.address}</td>
                <td className="btn btn-success btn-md">
                  <Link
                    style={{ color: "white" }}
                    to={`/update/${contact._id}`}
                  >
                    Update
                  </Link>
                </td>
                <td className="btn btn-danger">
                  <Link
                    onClick={this.onDelete.bind(this, contact._id)}
                    style={{ color: "white" }}
                    to="/"
                  >
                    Delete
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <ReactPaginate
          previousLabel={"previous"}
          nextLabel={"next"}
          breakLabel={"..."}
          breakClassName={"break-me"}
          pageCount={this.state.pageCount}
          marginPagesDisplayed={2}
          pageRangeDisplayed={5}
          onPageChange={this.handlePageClick}
          containerClassName={"pagination"}
          subContainerClassName={"pages pagination"}
          activeClassName={"active"}
        />
      </div>
    );
    var no_contact = (
      <div className="main">
        <h1 style={{ color: "##d3d3d3" }}>No Contacts Available.....</h1>
      </div>
    );
    return (
      <div className="main">
        <div className="container">
          <h2 style={{ color: "#495057" }}> All Contacts</h2>
          <hr />
          <Link to="/add">
            <div style={{ marginLeft: "980px" }}>
              <button className="btn btn-primary"> New Contact</button>
            </div>
          </Link>
          <br />
          {this.state.contacts.length > 0 ? contact : no_contact}
        </div>
      </div>
    );
  }
}

export default AllContacts;
